mapper.select:
select * from tb_goods_comment


        and storeId=#{storeId}


        and grade = #{grade}


        and state = #{state}


        and goodsId in (select id from tb_gooods where name like concat ('%', #{name}, '%'))



    limit #{currentPage}, #{pageSize}

select count(1) from tb_goods_comment


        and storeId=#{storeId}


        and grade = #{grade}


        and state = #{state}


        and goodsId in (select id from tb_gooods where name like concat ('%', #{name}, '%'))
